import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Location } from '@angular/common';
import * as pdfMake from "pdfmake/build/pdfmake";
import * as pdfFonts from 'pdfmake/build/vfs_fonts';

(<any>pdfMake).vfs = pdfFonts.pdfMake.vfs;

@Component({
  selector: 'app-pdf',
  templateUrl: './pdf.component.html',
  styleUrls: ['./pdf.component.scss']
})
export class PdfComponent implements OnInit {

  params?: string;
  data? : string;
  movie : string = "Batman";
  theater : string = "Tres Cruces";
  date : string = "28/11/2022";
  time : string = "23:00hs";

  constructor(private activeRoute: ActivatedRoute, private location : Location) { }

  ngOnInit(): void {
    this.params = this.activeRoute.snapshot.params['data'];
    this.data = this.params!.toString();
    this.createPdf();
    this.location.back();
  }

  createPdf() {
    const documentDefinition : any = { 
      footer: {
        columns: [
          { text: 'ting.uy', alignment: 'left', margin: [ 20, 10, 0, 0 ]},
          {
              image: 'data:image/png;base64,',
              width: 40,
              alignment: 'right',
              margin: [0, 0, 10, 0]
            }
        ]
      },
      content: [
        // IMAGEN TING
        {
          image: 'data:image/png;base64,',
          width: 150,
          alignment: 'center',
          margin: [0, 1, 0, 40]
        },
        // QR TITLE
        {
          text: 'TU CÓDIGO QR:',
          style: 'header',
          alignment: 'center',
          margin: [0, 0, 10, 10]
        },
        // QR CODE
        { 
          qr : this.data,
          fit : 200,
          alignment : 'center'
        },
        // QR CODE STRING
        {
          text: this.data,
          alignment: 'center',
          margin: [0, 10, 0, 0],
          color: '#696969'
        },

        // INFO DEL TICKET
        {
          text: 'INFORMACIÓN DEL TICKET:',
          alignment: 'left',
          margin: [0, 30, 0, 0],
          bold: true,
          fontSize: 16
        },
        // MOVIE
        {
          text: ['Pelicula: ',  {text:this.movie, bold: false}],
          alignment: 'left',
          margin: [0, 5, 0, 0],
          bold: true
        },
        // CINE
        {
          text: ['Cine: ',  {text:this.theater, bold: false}],
          alignment: 'left',
          margin: [0, 5, 0, 0],
          bold: true
        },
        // DATE
        {
          text: ['Fecha: ',  {text:this.date, bold: false}],
          alignment: 'left',
          margin: [0, 5, 0, 0],
          bold: true
        },
        // TIME
        {
          text: ['Hora: ',  {text:this.time, bold: false}],
          alignment: 'left',
          margin: [0, 5, 0, 0],
          bold: true
        }



      ]
    };

    const pdf = pdfMake.createPdf(documentDefinition);
    pdf.open();
  }
}
